'use strict'

const fetchImpl = require('undici/lib/fetch').fetch

module.exports.fetch = async function fetch (resource) {
  try {
    return await fetchImpl(...arguments)
  } catch (err) {
    Error.captureStackTrace(err, this)
    throw err
  }
}
module.exports.FormData = require('undici/lib/fetch/formdata').FormData
module.exports.Headers = require('undici/lib/fetch/headers').Headers
module.exports.Response = require('undici/lib/fetch/response').Response
module.exports.Request = require('undici/lib/fetch/request').Request
